mainPageScript:
function loadMainVideoPage() {
    try {
        let mMainVideoPage = {
            "slideVideos": [],
            "newPlay": [],
            "newMovie": [],
            "newSoap": [],
            "newVariety": [],
            "newAnim": []
        };

        function getVideos(ele) {
            let videoLis = ele.getElementsByTagName("li");
            let videos = [];
            for (let i = 0; i < videoLis.length; i++) {
                let videoItemContentEle = videoLis[i].getElementsByTagName("a")[0];
                let video = {"videoSourceName": "4k屋"};
                video.infoUrl = window.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
                video.name = videoItemContentEle.attributes["title"].value;
                try {
                    video.score = videoItemContentEle.getElementsByClassName("score")[0].textContent;
                } catch (e) {
                }
                try {
                    video.title = videoItemContentEle.getElementsByClassName("title")[0].textContent;
                } catch (e) {
                }
                try {
                    video.coverImageUrl = videoItemContentEle.getElementsByTagName("img")[0].attributes["src"].value;
                } catch (e) {
                }
                videos.push(video);
            }
            return videos;
        }

        let swiperItems = document.getElementsByClassName("focusList")[0].getElementsByTagName("a");
        for (let i = 0; i < swiperItems.length; i++) {
            let swiperItem = swiperItems[i];
            let img = swiperItem.getElementsByTagName("img")[0];
            let video = {};
            video.infoUrl = window.location.protocol + "//" + window.location.host + swiperItem.attributes["href"].value;
            video.coverImageUrl = img.attributes["src"].value;
            video.title = swiperItem.getElementsByClassName("sTxt")[0].textContent.replace("[", "").replace("]", "");
            mMainVideoPage.slideVideos.push(video);
        }

        mMainVideoPage.newMovie = getVideos(document.querySelector("body > div:nth-child(12) > div.all_tab"));
        mMainVideoPage.newSoap = getVideos(document.querySelector("body > div:nth-child(14) > div.all_tab"));
        mMainVideoPage.newVariety = getVideos(document.querySelector("body > div:nth-child(18) > div:nth-child(1)"));
        mMainVideoPage.newAnim = getVideos(document.querySelector("body > div:nth-child(16) > div:nth-child(1)"));
        console.log(JSON.stringify(mMainVideoPage));
        window.videoPluginEngine.sendMainVideoPage(JSON.stringify(mMainVideoPage));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadMainVideoPage();

videosPageScript:
function loadVideoListPage() {
    try {
        let mVideoPage = {"videoClassGroups": [], "videoList": [], "haveNextPage": false, "nextPageUrl": ""};
        let selectList = document.getElementsByClassName("selectList")[0];
        let selectGroupsLi = selectList.getElementsByTagName("li");
        for (let i = 0; selectGroupsLi.length > i; i++) {
            let mVideoClassGroup = {"videoClasses": []};
            let selects = selectGroupsLi[i].getElementsByTagName("a");
            for (let j = 0; j < selects.length; j++) {
                let mVideoClass = {
                    "pageUrl": document.location.protocol + "//" + window.location.host + selects[j].attributes["href"].value,
                    "name": selects[j].text
                };
                mVideoClassGroup.videoClasses.push(mVideoClass);
            }
            mVideoPage.videoClassGroups.push(mVideoClassGroup);
        }

        let videoItemEles = document.getElementById("vod_list").getElementsByTagName("li");

        for (let i = 0; i < videoItemEles.length; i++) {

            let videoItemContentEle = videoItemEles[i].getElementsByTagName("a")[0];

            let video = {};
            video.infoUrl = document.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
            video.name = videoItemContentEle.attributes["title"].value;
            try {
                video.score = videoItemContentEle.getElementsByClassName("score")[0].textContent;
            } catch (e) {
            }
            try {
                video.title = videoItemContentEle.getElementsByClassName("title")[0].textContent;
            } catch (e) {
            }
            video.coverImageUrl = videoItemContentEle.getElementsByTagName("img")[0].attributes["src"].value;
            mVideoPage.videoList.push(video);
        }
        let pagesEle = document.getElementsByClassName("ui-vpages")[0];
        if (pagesEle.getElementsByTagName("strong")[0] != null) {
            let pages = pagesEle.getElementsByTagName("strong")[0].innerText.trim().split(/\s+/)[1];
            let fullPage = pages.trim().split("/")[1];
            let currentPage = pages.trim().split("/")[0];
            mVideoPage.haveNextPage = currentPage / fullPage < 1;
            if (mVideoPage.haveNextPage) {
                mVideoPage.nextPageUrl = document.location.protocol + "//" + window.location.host + pagesEle.getElementsByClassName("pagegbk")[0].attributes["href"].value;
            }
        } else {
            mVideoPage.haveNextPage = false
        }
        console.log(JSON.stringify(mVideoPage));
        window.videoPluginEngine.sendVideoPage(JSON.stringify(mVideoPage));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadVideoListPage();

searchPageScript:
function loadSearchPage() {
    try {
        let mSearchPage = {"videoList": [], "haveNextPage": false, "nextPageUrl": ""};
        let videosItemElems = document.getElementsByClassName("new_tab_img")[0].getElementsByTagName("li");
        for (let i = 0; i < videosItemElems.length; i++) {
            let videoItemContentEle = videosItemElems[i].getElementsByTagName("a")[0];
            let video = {"videoSourceName": "4k屋"};
            let videoInfo = videosItemElems[i].getElementsByClassName("list_info")[0].textContent;
            video.infoUrl = document.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
            video.name = videoItemContentEle.attributes["title"].value;
            video.coverImageUrl = videoItemContentEle.getElementsByTagName("img")[0].attributes["src"].value;
            video.videoClass = videoInfo.split("分类：")[1].split("类型：")[0];
            video.types = videoInfo.split("类型：")[1].split("演员：")[0];
            video.stars = videoInfo.split("演员：")[1].split("状态：")[0];
            video.status = videoInfo.split("状态：")[1].split("时间：")[0];
            video.updateTime = videoInfo.split("时间：")[1];
            mSearchPage.videoList.push(video)
        }

        let searchPagesEle = document.getElementsByClassName("ui-vpages")[0];
        let pages = null;
        try {
            pages = searchPagesEle.getElementsByTagName("strong")[0].innerText.trim().split(/\s+/)[1];
        } catch (e) {
        }

        mSearchPage.haveNextPage = false;
        mSearchPage.nextPageUrl = "";
        if (pages != null) {
            let fullPage = pages.trim().split("/")[1];
            let currentPage = pages.trim().split("/")[0];
            mSearchPage.haveNextPage = currentPage / fullPage < 1;
            if (mSearchPage.haveNextPage) {
                mSearchPage.nextPageUrl = document.location.protocol + "//" + window.location.host + searchPagesEle.getElementsByClassName("pagegbk")[0].attributes["href"].value;
            }
        }
        console.log(JSON.stringify(mSearchPage));
        window.videoPluginEngine.sendSearchVideoPage(JSON.stringify(mSearchPage));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadSearchPage();

playPageScript:
function loadInfoPage() {
    try {
        const mVideoInfo = {
            "name": "",
            "title": "",
            "status": "",
            "coverImageUrl": "",
            "playLineList": [],
            "videoClass": "",
            "types": "",
            "score": "",
            "infoUrl": "",
            "stars": "",
            "director": "",
            "updateTime": "",
            "showTime": "",
            "introduction": "",
            "area": "",
            "language": "",
            "headerTime": 0,
            "tailTime": 0,
            "videoType": -1,
        };
        mVideoInfo.coverImageUrl = document.getElementsByClassName("vod-n-img")[0].getElementsByTagName("img")[0].attributes["src"].value;
        const videoInfoText = document.getElementsByClassName("vod-n-l")[0].textContent;
        mVideoInfo.name = videoInfoText.split("状态：")[0];
        mVideoInfo.title = videoInfoText.split("状态：")[1].split("主演：")[0];
        mVideoInfo.stars = videoInfoText.split("主演：")[1].split("类型：")[0];
        mVideoInfo.types = videoInfoText.split("类型：")[1].split("导演：")[0];
        mVideoInfo.director = videoInfoText.split("导演：")[1].split("地区：")[0];
        mVideoInfo.area = videoInfoText.split("地区：")[1].split("更新：")[0];
        mVideoInfo.updateTime = videoInfoText.split("更新：")[1].split("简介：")[0];
        mVideoInfo.introduction = document.getElementsByClassName("vod_content")[0].textContent;
        let playLineNames = [];
        let playLineInfo = document.getElementsByClassName("vod-info-tab")[0];
        let playLineNameElements = playLineInfo.getElementsByTagName("a");
        for (let i = 0; i < playLineNameElements.length; i++) {
            playLineNames.push(playLineNameElements[i].textContent);
        }
        let playLineEpisodeInfoElements = playLineInfo.getElementsByClassName("play-box");
        for (let i = 0; i < playLineEpisodeInfoElements.length; i++) {
            let episodeItemEpisodeElements = playLineEpisodeInfoElements[i].getElementsByClassName("plau-ul-list")[0].getElementsByTagName("li");
            const mPlayLine = {"videoEpisodeList": []};
            mPlayLine.name = playLineNames[i];
            for (let i = episodeItemEpisodeElements.length - 1; i > -1; i--) {
                const a = episodeItemEpisodeElements[i].getElementsByTagName("a")[0];
                const mVideoEpisode = {"name": "", "url": ""};
                mVideoEpisode.name = a.attributes["title"].value;
                mVideoEpisode.url = document.location.protocol + "//" + window.location.host + a.attributes["href"].value;
                mPlayLine.videoEpisodeList.push(mVideoEpisode);
            }
            mVideoInfo.playLineList.push(mPlayLine);
        }
        console.log(JSON.stringify(mVideoInfo));
        window.videoPluginEngine.sendVideoInfo(JSON.stringify(mVideoInfo));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadInfoPage();
