function loadMainVideoPage() {
    try {
        let mMainVideoPage = {
            "slideVideos": [],
            "newPlay": [],
            "newMovie": [],
            "newSoap": [],
            "newVariety": [],
            "newAnim": []
        };

        function getVideos(ele) {
            let videoLis = ele.getElementsByClassName("fed-list-info")[0].getElementsByTagName("li");
            let videos = [];
            for (let i = 0; i < videoLis.length; i++) {
                let videoItemContentEle = videoLis[i].getElementsByTagName("a")[0];
                let video = {};
                video.infoUrl = window.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
                video.title = videoItemContentEle.getElementsByClassName("fed-list-remarks")[0].textContent;
                video.score = videoItemContentEle.getElementsByClassName("fed-list-score")[0].textContent;
                try {
                    video.name = videoLis[i].getElementsByClassName("fed-list-title")[0].textContent;
                } catch (e) {
                }
                try {
                    video.coverImageUrl = videoItemContentEle.attributes["data-original"].value;
                } catch (e) {
                }
                videos.push(video);
            }
            return videos;
        }

        let swiperItems = document.getElementsByClassName("fed-swip-container")[0].getElementsByTagName("ul")[0].getElementsByTagName("li");
        for (let i = 0; i < swiperItems.length; i++) {
            let swiperItem = swiperItems[i];
            let swiperItemA = swiperItem.getElementsByTagName("a")[0];
            let video = {"videoSourceName": "初心视频"};
            video.infoUrl = window.location.protocol + "//" + window.location.host + swiperItemA.attributes["href"].value;
            video.coverImageUrl = swiperItemA.attributes["style"].value.toString().split("url(\"")[1].toString().split("\");")[0];
            video.title = swiperItemA.getElementsByClassName("fed-part-eone")[0].textContent.trimStart().trimEnd().replace("\n"," ").replace(/[\'\"\\\/\b\f\n\r\t]/g, '');
            mMainVideoPage.slideVideos.push(video);
        }

        mMainVideoPage.newPlay = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(2)"));
        mMainVideoPage.newMovie = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(4)"));
        mMainVideoPage.newSoap = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(5)"));
        mMainVideoPage.newVariety = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(6)"));
        mMainVideoPage.newAnim = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(7)"));
        console.log(JSON.stringify(mMainVideoPage));
        try {
            window.videoPluginEngine.sendMainVideoPage(JSON.stringify(mMainVideoPage));
        } catch (e) {
        }
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}

loadMainVideoPage();

searchPageScript:
function loadSearchPage() {
    try {
        let mSearchPage = {"videoList": [], "haveNextPage": false, "nextPageUrl": ""};
        let videosItemElems = document.querySelector("body > div.fed-main-info.fed-min-width > div > div").getElementsByTagName("dl");
        for (let i = 0; i < videosItemElems.length; i++) {
            let dt = videosItemElems[i].getElementsByTagName("dt")[0];
            let dd = videosItemElems[i].getElementsByTagName("dd")[0];
            let videoItemContentEleDtA = dt.getElementsByTagName("a")[0];
            let video = {"videoSourceName": "初心视频"};
            video.infoUrl = document.location.protocol + "//" + window.location.host + dt.getElementsByTagName("a")[0].attributes["href"].value;
            video.score = videoItemContentEleDtA.getElementsByClassName("fed-list-score")[0].textContent.trimStart().trimEnd();
            video.name = dd.getElementsByClassName("fed-part-eone")[0].getElementsByTagName("a")[0].textContent.trimStart().trimEnd();
            video.coverImageUrl = videoItemContentEleDtA.attributes["data-original"].value;
            video.title = videoItemContentEleDtA.getElementsByClassName("fed-list-remarks")[0].textContent.trimStart().trimEnd();
            let infos = dd.getElementsByClassName("fed-part-rows")[0].textContent;
            video.stars = infos.split("主演：")[1].split('导演：')[0].trimStart().trimEnd();
            video.Director = infos.split("导演：")[1].split('分类：')[0].trimStart().trimEnd();
            video.types = infos.split("分类：")[1].split('地区：')[0].trimStart().trimEnd();
            video.area = infos.split("地区：")[1].split('年份：')[0].trimStart().trimEnd();
            video.showTime = infos.split("年份：")[1].split('更新：')[0].trimStart().trimEnd();
            video.updateTime = infos.split("更新：")[1].split('简介：')[0].trimStart().trimEnd();
            mSearchPage.videoList.push(video)
        }

        let pagesEle = document.getElementsByClassName("fed-page-info")[0];
        mSearchPage.haveNextPage = false;
        if (pagesEle !== undefined) {
            let pages = pagesEle.getElementsByTagName("a");
            for (let i = 0; i < pages.length; i++) {
                if (pages[i].textContent.indexOf("/") !== -1) {
                    let fullNum = pages[i].textContent.split("/")[1];
                    let currentNum = pages[i].textContent.split("/")[0];
                    mSearchPage.haveNextPage = currentNum / fullNum < 1;
                }
            }

            if (mSearchPage.haveNextPage) {
                for (let i = 0; i < pages.length; i++) {
                    if (pages[i].textContent === "下页") {
                        mSearchPage.nextPageUrl = document.location.protocol + "//" + window.location.host + pages[i].attributes["href"].value;
                    }
                }
            }
        }

        console.log(JSON.stringify(mSearchPage));
        try {
            window.videoPluginEngine.sendSearchVideoPage(JSON.stringify(mSearchPage));
        } catch (e) {
        }
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}

loadSearchPage();

videosPageScript:
function loadVideoListPage() {
    try {
        let mVideoPage = {"videoClassGroups": [], "videoList": [], "haveNextPage": false, "nextPageUrl": ""};
        var selectList = document.getElementsByClassName("fed-casc-info")[0];
        if (selectList===undefined||selectList===null){
            selectList = document.getElementsByClassName("fed-scre-list")[0]
        }

        let selectGroupsLi = selectList.getElementsByTagName("dl");
        for (let i = 0; selectGroupsLi.length > i; i++) {
            let mVideoClassGroup = {"videoClasses": []};
            let selects = selectGroupsLi[i].getElementsByTagName("a");
            for (let j = 0; j < selects.length; j++) {
                let mVideoClass = {
                    "pageUrl": document.location.protocol + "//" + window.location.host + selects[j].attributes["href"].value,
                    "name": selects[j].text.trimStart().trimEnd()
                };
                mVideoClassGroup.videoClasses.push(mVideoClass);
            }
            mVideoPage.videoClassGroups.push(mVideoClassGroup);
        }

        let videoItemEles = document.getElementsByClassName("fed-list-info")[0].getElementsByTagName("li");

        for (let i = 0; i < videoItemEles.length; i++) {

            let videoItemContentEle = videoItemEles[i].getElementsByTagName("a")[0];

            let video = {};
            video.infoUrl = document.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
            video.name = videoItemEles[i].getElementsByClassName("fed-list-title")[0].textContent;
            video.score = videoItemContentEle.getElementsByClassName("fed-list-score")[0].textContent;
            video.title = videoItemContentEle.getElementsByClassName("fed-list-remarks")[0].textContent;

            video.coverImageUrl = videoItemContentEle.attributes["data-original"].value;

            mVideoPage.videoList.push(video);
        }
        let pagesEle = document.getElementsByClassName("fed-page-info")[0];
        let pages = pagesEle.getElementsByTagName("a");

        mVideoPage.haveNextPage = false;
        for (let i = 0; i < pages.length; i++) {
            if (pages[i].textContent.indexOf("/") !== -1) {
                let fullNum = pages[i].textContent.split("/")[1];
                let currentNum = pages[i].textContent.split("/")[0];
                mVideoPage.haveNextPage = currentNum / fullNum < 1;
            }
        }

        if (mVideoPage.haveNextPage) {
            for (let i = 0; i < pages.length; i++) {
                if (pages[i].textContent === "下页") {
                    mVideoPage.nextPageUrl = document.location.protocol + "//" + window.location.host + pages[i].attributes["href"].value;
                }
            }
        }
        console.log(JSON.stringify(mVideoPage));
        try {
            window.videoPluginEngine.sendVideoPage(JSON.stringify(mVideoPage));
        } catch (e) {
        }
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}

loadVideoListPage();

playPageScript:
function loadInfoPage() {
    try {
        const mVideoInfo = {
            "name": "",
            "title": "",
            "status": "",
            "coverImageUrl": "",
            "playLineList": [],
            "videoClass": "",
            "types": "",
            "score": "",
            "infoUrl": "",
            "stars": "",
            "director": "",
            "updateTime": "",
            "showTime": "",
            "introduction": "",
            "area": "",
            "language": "",
            "headerTime": 0,
            "tailTime": 0,
            "videoType": -1,
        };
        mVideoInfo.coverImageUrl = document.getElementsByClassName("fed-deta-images")[0].getElementsByTagName("a")[0].attributes["data-original"].value;
        mVideoInfo.status = mVideoInfo.score = mVideoInfo.title = document.getElementsByClassName("fed-deta-images")[0].getElementsByTagName("a")[0].getElementsByClassName("fed-list-score")[0].textContent;

        const videoInfoText = document.getElementsByClassName("fed-deta-content")[0].textContent;

        mVideoInfo.name = videoInfoText.split("主演：")[0].trimEnd().trimStart();
        mVideoInfo.director = videoInfoText.split("导演：")[1].split("分类：")[0].trimEnd().trimStart();
        mVideoInfo.stars = videoInfoText.split("主演：")[1].split("导演：")[0].trimEnd().trimStart();
        mVideoInfo.types = videoInfoText.split("分类：")[1].split("地区：")[0].trimEnd().trimStart();
        mVideoInfo.area = videoInfoText.split("地区：")[1].split("年份：")[0].trimEnd().trimStart();
        mVideoInfo.showTime = videoInfoText.split("年份：")[1].split("更新：")[0].trimEnd().trimStart();
        mVideoInfo.updateTime = videoInfoText.split("更新：")[1].split("简介：")[0].trimEnd().trimStart();
        mVideoInfo.introduction = "  " + document.getElementsByClassName("fed-tabs-boxs")[0].getElementsByClassName("fed-tabs-item")[1].textContent.trimStart().trimEnd();

        let playLineEpisodeInfoElements = document.getElementsByClassName("fed-drop-boxs")[1].getElementsByClassName("fed-play-item");
        for (let i = 0; i < playLineEpisodeInfoElements.length; i++) {
            let episodeItemEpisodeElements = playLineEpisodeInfoElements[i].getElementsByClassName("fed-part-rows")[1].getElementsByTagName("li");
            const mPlayLine = {"videoEpisodeList": []};
            for (let i = 0; i < episodeItemEpisodeElements.length; i++) {
                const a1 = episodeItemEpisodeElements[i].getElementsByTagName("a")[0];
                const mVideoEpisode = {"name": "", "url": ""};
                mVideoEpisode.name = a1.text;
                mVideoEpisode.url = document.location.protocol + "//" + window.location.host + a1.attributes["href"].value;
                mPlayLine.videoEpisodeList.push(mVideoEpisode);
            }
            mVideoInfo.playLineList.push(mPlayLine);
        }
        console.log(JSON.stringify(mVideoInfo));
        try {
            window.videoPluginEngine.sendVideoInfo(JSON.stringify(mVideoInfo));
        } catch (e) {
        }
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}

loadInfoPage();
