mainPageScript:
function loadMainVideoPage() {
    try {
        let mMainVideoPage = {
            "slideVideos": [],
            "newPlay": [],
            "newMovie": [],
            "newSoap": [],
            "newVariety": [],
            "newAnim": []
        };

        function getVideos(ele) {
            let videoLis = ele.getElementsByTagName("li");
            let videos = [];
            for (let i = 0; i < videoLis.length; i++) {
                let li = videoLis[i];
                let videoItemContentEleAPic = li.getElementsByClassName("fed-list-pics")[0];
                let video = {};
                video.infoUrl = window.location.protocol + "//" + window.location.host + videoItemContentEleAPic.attributes["href"].value;
                video.name = li.getElementsByClassName("fed-list-title")[0].textContent;
                try {
                    video.score = videoItemContentEleAPic.getElementsByClassName("fed-list-score")[0].textContent;
                } catch (e) {
                }
                try {
                    video.title = videoItemContentEleAPic.getElementsByClassName("fed-list-remarks")[0].textContent;
                } catch (e) {
                }
                try {
                    video.coverImageUrl = window.location.protocol + "//" + window.location.host + videoItemContentEleAPic.attributes["data-original"].value;
                } catch (e) {
                }
                videos.push(video);
            }
            return videos;
        }

        let swiperItems = document.querySelector("body > div.fed-main-info.fed-min-width > div > div.fed-swip-container.fed-part-rows.swiper-container-horizontal.swiper-container-android > ul").getElementsByTagName("a");
        for (let i = 0; i < swiperItems.length; i++) {
            let swiperItem = swiperItems[i];
            let video = {"videoSourceName": "小莫影院"};
            video.infoUrl = window.location.protocol + "//" + window.location.host + swiperItem.attributes["href"].value;
            try {
                video.coverImageUrl = window.location.protocol + "//" + window.location.host + swiperItem.attributes["style"].value.split("url(\"")[1].split("\")")[0];
            } catch (e) {
                video.coverImageUrl = window.location.protocol + "//" + window.location.host + swiperItem.attributes["data-background"].value;
            }
            video.title = swiperItem.getElementsByClassName("fed-swip-head")[0].textContent + " " + swiperItem.getElementsByClassName("fed-font-xii")[0].textContent;
            mMainVideoPage.slideVideos.push(video);
        }
        mMainVideoPage.newPlay = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(5) > ul"));
        mMainVideoPage.newMovie = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(6) > div.fed-col-sx12.fed-col-md7 > ul"));
        mMainVideoPage.newSoap = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(7) > div.fed-col-sx12.fed-col-md7 > ul"));
        mMainVideoPage.newVariety = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(9) > div:nth-child(2) > ul"));
        mMainVideoPage.newAnim = getVideos(document.querySelector("body > div.fed-main-info.fed-min-width > div > div:nth-child(8) > div.fed-col-sx12.fed-col-md7 > ul"));
        console.log(JSON.stringify(mMainVideoPage));
        window.videoPluginEngine.sendMainVideoPage(JSON.stringify(mMainVideoPage));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadMainVideoPage();

videosPageScript:
function loadVideoList() {
    try {
        let mVideoPage = {"videoClassGroups": [], "videoList": [], "haveNextPage": false, "nextPageUrl": ""};

        let selectList = document.getElementsByClassName("fed-casc-list")[0];
        let selectGroupsLi = selectList.getElementsByTagName("dl");
        for (let i = 0; selectGroupsLi.length > i; i++) {
            let mVideoClassGroup = {"videoClasses": []};
            let selects = selectGroupsLi[i].getElementsByTagName("a");
            for (let j = 0; j < selects.length; j++) {
                let mVideoClass = {
                    "pageUrl": document.location.protocol + "//" + window.location.host + selects[j].attributes["href"].value,
                    "name": selects[j].text
                };
                mVideoClassGroup.videoClasses.push(mVideoClass);
            }
            mVideoPage.videoClassGroups.push(mVideoClassGroup);
        }

        let videoItemEles = document.getElementsByClassName("fed-list-info")[0].getElementsByTagName("li");

        for (let i = 0; i < videoItemEles.length; i++) {

            let videoItemContentEle = videoItemEles[i].getElementsByClassName("fed-list-pics")[0];

            let video = {};
            video.infoUrl = document.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["href"].value;
            video.name = videoItemEles[i].getElementsByClassName("fed-list-title")[0].textContent;
            try {
                video.score = videoItemContentEle.getElementsByClassName("fed-list-score")[0].textContent;
            } catch (e) {
            }
            try {
                video.title = videoItemContentEle.getElementsByClassName("fed-list-remarks")[0].textContent;
            } catch (e) {
            }
            video.coverImageUrl = document.location.protocol + "//" + window.location.host + videoItemContentEle.attributes["data-original"].value;
            mVideoPage.videoList.push(video);
        }
        let pagesEleAs = document.getElementsByClassName("fed-page-info")[0].getElementsByTagName("a");
        for (let i = 0; i < pagesEleAs.length; i++) {
            if (pagesEleAs[i].textContent.indexOf("/") !== -1) {
                mVideoPage.haveNextPage = pagesEleAs[i].textContent.split("/")[0] / pagesEleAs[i].textContent.split("/")[1] < 1
            }else if (pagesEleAs[i].textContent==="下页"){
                mVideoPage.nextPageUrl = document.location.protocol + "//" + window.location.host + pagesEleAs[i].attributes["href"].value;
            }
        }
        if (!mVideoPage.haveNextPage) {
            mVideoPage.nextPageUrl = "";
        }
        console.log(JSON.stringify(mVideoPage));
        window.videoPluginEngine.sendVideoPage(JSON.stringify(mVideoPage));
    } catch (e) {
        window.videoPluginEngine.sendError(e.toString());
    }
}
loadVideoList();

searchPageScript:
function loadSearchPage() {
    try {
        let mSearchPage = {"videoList": [], "haveNextPage": false, "nextPageUrl": ""};
        let videosItemElems = document.querySelector("body > div.fed-main-info.fed-min-width > div > div").getElementsByTagName("dl");
        for (let i = 0; i < videosItemElems.length; i++) {
            let videoItemAPic = videosItemElems[i].getElementsByClassName("fed-list-pics")[0];
            let video = {"videoSourceName": "小莫影院"};
            let videoInfo = videosItemElems[i].getElementsByTagName("dd")[0].textContent;
            video.infoUrl = document.location.protocol + "//" + window.location.host + videoItemAPic.attributes["href"].value;
            video.name = videosItemElems[i].getElementsByTagName("dd")[0].getElementsByTagName("h1")[0].textContent.trimEnd().trimStart();
